INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, tags) VALUES
('O que é Machine Learning?', '["Programação manual de regras", "Algoritmos que aprendem com dados", "Linguagem de programação", "Sistema operacional"]', 'Algoritmos que aprendem com dados', 'Machine Learning permite que sistemas aprendam automaticamente a partir de dados sem serem explicitamente programados.', 'medium', 'technology', '["ia", "machine learning"]'),
('Qual a diferença entre AI e ML?', '["São a mesma coisa", "AI é mais amplo que ML", "ML é mais amplo que AI", "Não há relação"]', 'AI é mais amplo que ML', 'Inteligência Artificial é o campo geral, enquanto Machine Learning é uma subcategoria da AI.', 'easy', 'technology', '["ia", "conceitos"]'),
('O que são Redes Neurais?', '["Cabos de internet", "Modelos inspirados no cérebro", "Protocolos de comunicação", "Bancos de dados"]', 'Modelos inspirados no cérebro', 'Redes neurais são modelos computacionais inspirados na estrutura e funcionamento dos neurônios biológicos.', 'medium', 'technology', '["redes neurais", "ia"]'),
('O que é Deep Learning?', '["Aprender mergulho", "ML com redes neurais profundas", "Programação baixo nível", "Análise superficial"]', 'ML com redes neurais profundas', 'Deep Learning usa redes neurais com múltiplas camadas para aprender representações complexas dos dados.', 'hard', 'technology', '["deep learning", "redes neurais"]'),
('Para que serve o Big Data?', '["Armazenar fotos", "Analisar grandes volumes de dados", "Jogar games", "Editar vídeos"]', 'Analisar grandes volumes de dados', 'Big Data refere-se a conjuntos de dados muito grandes que requerem ferramentas especiais para análise.', 'easy', 'technology', '["big data", "dados"]'),
('O que é Blockchain?', '["Moeda digital", "Livro-razão distribuído", "Aplicativo móvel", "Sistema bancário"]', 'Livro-razão distribuído', 'Blockchain é uma tecnologia de livro-razão distribuído que mantém registros seguros e imutáveis.', 'medium', 'technology', '["blockchain", "tecnologia"]'),
('O que é IoT?', '["Internet das Coisas", "Sistema operacional", "Linguagem de programação", "Protocolo de rede"]', 'Internet das Coisas', 'IoT conecta dispositivos físicos à internet, permitindo coleta e troca de dados.', 'easy', 'technology', '["iot", "conectividade"]'),
('O que é Cloud Computing?', '["Previsão do tempo", "Computação em nuvem", "Armazenamento local", "Software desktop"]', 'Computação em nuvem', 'Cloud Computing fornece recursos computacionais através da internet, como servidores, armazenamento e aplicações.', 'easy', 'technology', '["cloud", "computacao"]'),
('O que é API?', '["Interface de Programação", "Algoritmo avançado", "Protocolo de segurança", "Banco de dados"]', 'Interface de Programação', 'API permite que diferentes softwares se comuniquem entre si através de interfaces bem definidas.', 'medium', 'technology', '["api", "programacao"]'),
('O que é Cybersecurity?', '["Jogo de computador", "Segurança cibernética", "Sistema operacional", "Rede social"]', 'Segurança cibernética', 'Cybersecurity protege sistemas, redes e programas de ataques digitais.', 'easy', 'technology', '["seguranca", "cyber"]);
INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, tags) VALUES
('O que é Day Trading?', '["Negociar apenas de dia", "Comprar e vender no mesmo dia", "Trading a longo prazo", "Investir em ações"]', 'Comprar e vender no mesmo dia', 'Day Trading envolve a compra e venda de ativos financeiros dentro do mesmo dia de negociação.', 'easy', 'trading', '["day trading", "estrategias"]'),
('O que é Stop Loss?', '["Ganho máximo", "Ordem de proteção contra perdas", "Taxa de corretagem", "Dividendo"]', 'Ordem de proteção contra perdas', 'Stop Loss é uma ordem para vender um ativo quando atinge um preço predeterminado para limitar perdas.', 'medium', 'trading', '["stop loss", "gestao risco"]'),
('O que significa "Bull Market"?', '["Mercado em baixa", "Mercado em alta", "Mercado lateral", "Mercado volátil"]', 'Mercado em alta', 'Bull Market refere-se a um período de crescimento contínuo nos preços dos ativos.', 'easy', 'trading', '["bull market", "tendencias"]'),
('O que é Análise Técnica?', '["Análise de fundamentos", "Estudo de gráficos e preços", "Análise de balanços", "Estudo macroeconômico"]', 'Estudo de gráficos e preços', 'Análise Técnica usa histórico de preços e volume para prever movimentos futuros.', 'medium', 'trading', '["analise tecnica", "graficos"]'),
('O que é Leverage?', '["Alavancagem financeira", "Taxa de juros", "Tipo de ação", "Estratégia conservadora"]', 'Alavancagem financeira', 'Leverage permite negociar com mais capital do que se possui, amplificando ganhos e perdas.', 'hard', 'trading', '["leverage", "alavancagem"]'),
('O que é Swing Trading?', '["Trading intraday", "Posições por dias/semanas", "Trading de alta frequência", "Investimento passivo"]', 'Posições por dias/semanas', 'Swing Trading mantém posições por alguns dias ou semanas para capturar movimentos de preço.', 'medium', 'trading', '["swing trading", "estrategias"]'),
('O que é Spread?', '["Diferença entre compra e venda", "Taxa de corretagem", "Dividendo", "Valor patrimonial"]', 'Diferença entre compra e venda', 'Spread é a diferença entre o preço de compra (bid) e venda (ask) de um ativo.', 'easy', 'trading', '["spread", "mercado"]'),
('O que é Volume?', '["Preço do ativo", "Quantidade negociada", "Taxa de juros", "Volatilidade"]', 'Quantidade negociada', 'Volume representa a quantidade de ações ou contratos negociados em um período.', 'easy', 'trading', '["volume", "liquidez"]'),
('O que é Volatilidade?', '["Estabilidade de preços", "Variação dos preços", "Tendência de alta", "Dividendos"]', 'Variação dos preços', 'Volatilidade mede o grau de variação dos preços de um ativo ao longo do tempo.', 'medium', 'trading', '["volatilidade", "risco"]'),
('O que é Scalping?', '["Trading de longo prazo", "Trading de curtíssimo prazo", "Análise fundamentalista", "Investimento passivo"]', 'Trading de curtíssimo prazo', 'Scalping busca lucros pequenos em operações muito rápidas, geralmente em minutos.', 'hard', 'trading', '["scalping", "estrategias"]);
INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, tags) VALUES
('Quem criou o Bitcoin?', '["Vitalik Buterin", "Satoshi Nakamoto", "Charlie Lee", "Gavin Andresen"]', 'Satoshi Nakamoto', 'Satoshi Nakamoto é o pseudônimo da pessoa ou grupo que criou o Bitcoin.', 'easy', 'cryptocurrency', '["bitcoin", "historia"]'),
('O que é uma Wallet?', '["Carteira física", "Carteira digital para criptomoedas", "Conta bancária", "Aplicativo de pagamento"]', 'Carteira digital para criptomoedas', 'Wallet é um software ou hardware que armazena chaves privadas para acessar criptomoedas.', 'easy', 'cryptocurrency', '["wallet", "seguranca"]'),
('O que é Mining?', '["Comprar criptomoedas", "Validar transações na blockchain", "Vender criptomoedas", "Criar exchanges"]', 'Validar transações na blockchain', 'Mining é o processo de validar transações e adicionar novos blocos à blockchain.', 'medium', 'cryptocurrency', '["mining", "blockchain"]'),
('O que é uma Exchange?', '["Banco tradicional", "Plataforma para negociar cripto", "Carteira digital", "Protocolo blockchain"]', 'Plataforma para negociar cripto', 'Exchange é uma plataforma que permite comprar, vender e trocar criptomoedas.', 'easy', 'cryptocurrency', '["exchange", "trading"]'),
('O que é DeFi?', '["Bitcoin", "Finanças Descentralizadas", "Tipo de carteira", "Exchange centralizada"]', 'Finanças Descentralizadas', 'DeFi refere-se a aplicações financeiras construídas em blockchains descentralizadas.', 'medium', 'cryptocurrency', '["defi", "descentralizacao"]'),
('O que é um Smart Contract?', '["Contrato físico", "Contrato auto-executável na blockchain", "Acordo bancário", "Aplicativo móvel"]', 'Contrato auto-executável na blockchain', 'Smart Contracts são programas que executam automaticamente quando condições predefinidas são atendidas.', 'hard', 'cryptocurrency', '["smart contracts", "ethereum"]'),
('O que é Staking?', '["Minerar Bitcoin", "Participar na validação da rede", "Vender criptomoedas", "Criar tokens"]', 'Participar na validação da rede', 'Staking envolve "travar" criptomoedas para participar da validação de transações e ganhar recompensas.', 'medium', 'cryptocurrency', '["staking", "pos"]'),
('O que é um NFT?', '["Moeda digital", "Token Não-Fungível", "Protocolo blockchain", "Tipo de mineração"]', 'Token Não-Fungível', 'NFT representa a propriedade de itens digitais únicos na blockchain.', 'easy', 'cryptocurrency', '["nft", "tokens"]'),
('O que é Ethereum?', '["Fork do Bitcoin", "Plataforma para smart contracts", "Exchange", "Carteira digital"]', 'Plataforma para smart contracts', 'Ethereum é uma plataforma blockchain que permite a criação de smart contracts e aplicações descentralizadas.', 'medium', 'cryptocurrency', '["ethereum", "plataforma"]'),
('O que é HODL?', '["Tipo de trading", "Estratégia de longo prazo", "Exchange", "Protocolo"]', 'Estratégia de longo prazo', 'HODL significa "Hold On for Dear Life" - estratégia de manter criptomoedas por longo prazo.', 'easy', 'cryptocurrency', '["hodl", "estrategia"]);
INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, tags) VALUES
('O que é diversificação?', '["Concentrar em um ativo", "Distribuir investimentos", "Vender tudo", "Comprar apenas ações"]', 'Distribuir investimentos', 'Diversificação reduz risco distribuindo investimentos entre diferentes ativos e classes.', 'easy', 'portfolio', '["diversificacao", "risco"]'),
('O que é Asset Allocation?', '["Venda de ativos", "Alocação de ativos", "Compra de ações", "Análise técnica"]', 'Alocação de ativos', 'Asset Allocation é a estratégia de dividir investimentos entre diferentes categorias de ativos.', 'medium', 'portfolio', '["alocacao", "estrategia"]'),
('O que é Rebalanceamento?', '["Vender tudo", "Ajustar proporções do portfólio", "Comprar mais ações", "Calcular juros"]', 'Ajustar proporções do portfólio', 'Rebalanceamento restaura as proporções originais dos ativos no portfólio.', 'medium', 'portfolio', '["rebalanceamento", "gestao"]'),
('O que é Correlação?', '["Preço de ativos", "Relação entre movimentos", "Taxa de retorno", "Dividendos"]', 'Relação entre movimentos', 'Correlação mede como dois ativos se movem em relação um ao outro.', 'hard', 'portfolio', '["correlacao", "analise"]'),
('O que é Sharpe Ratio?', '["Taxa de juros", "Medida de retorno ajustada ao risco", "Preço de ações", "Volume de negociação"]', 'Medida de retorno ajustada ao risco', 'Sharpe Ratio mede o retorno em excesso por unidade de risco do investimento.', 'hard', 'portfolio', '["sharpe", "metricas"]'),
('O que é Beta?', '["Tipo de ação", "Medida de volatilidade relativa", "Taxa de dividendos", "Valor patrimonial"]', 'Medida de volatilidade relativa', 'Beta mede a volatilidade de um ativo em relação ao mercado como um todo.', 'medium', 'portfolio', '["beta", "volatilidade"]'),
('O que é Duration?', '["Prazo do investimento", "Sensibilidade a juros", "Taxa de retorno", "Valor presente"]', 'Sensibilidade a juros', 'Duration mede a sensibilidade do preço de um título às mudanças nas taxas de juros.', 'hard', 'portfolio', '["duration", "renda fixa"]'),
('O que é Value at Risk (VaR)?', '["Valor do portfólio", "Perda máxima esperada", "Ganho mínimo", "Taxa de juros"]', 'Perda máxima esperada', 'VaR estima a perda máxima esperada de um portfólio em um período e nível de confiança específicos.', 'hard', 'portfolio', '["var", "risco"]'),
('O que é Alpha?', '["Primeira ação", "Retorno em excesso", "Taxa de juros", "Dividendo"]', 'Retorno em excesso', 'Alpha mede o retorno de um investimento acima do retorno esperado baseado no seu risco (beta).', 'hard', 'portfolio', '["alpha", "performance"]'),
('O que é Modern Portfolio Theory?', '["Teoria antiga", "Otimização risco-retorno", "Análise técnica", "Teoria de jogos"]', 'Otimização risco-retorno', 'MPT busca maximizar retorno para um dado nível de risco através da diversificação.', 'hard', 'portfolio', '["mpt", "teoria"]);
INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, tags) VALUES
('O que é Taxa Selic?', '["Taxa de câmbio", "Taxa básica de juros", "Taxa de inflação", "Taxa de desemprego"]', 'Taxa básica de juros', 'Selic é a taxa básica de juros da economia brasileira, definida pelo Banco Central.', 'easy', 'comprehensive', '["selic", "juros"]'),
('O que é Inflação?', '["Aumento de preços", "Queda de preços", "Estabilidade", "Taxa de juros"]', 'Aumento de preços', 'Inflação é o aumento generalizado e contínuo dos preços na economia.', 'easy', 'comprehensive', '["inflacao", "economia"]'),
('O que é PIB?', '["Produto Interno Bruto", "Plano de Investimento", "Política Monetária", "Preço de Ações"]', 'Produto Interno Bruto', 'PIB mede o valor total de bens e serviços produzidos em um país.', 'easy', 'comprehensive', '["pib", "economia"]'),
('O que é CDI?', '["Certificado de Depósito", "Conta Digital", "Cartão de Crédito", "Câmbio Dollar"]', 'Certificado de Depósito', 'CDI é a taxa média de empréstimos entre bancos e referência para investimentos.', 'medium', 'comprehensive', '["cdi", "investimentos"]'),
('O que é Tesouro Direto?', '["Banco privado", "Programa de títulos públicos", "Fundo de investimento", "Corretora"]', 'Programa de títulos públicos', 'Tesouro Direto permite que pessoas físicas comprem títulos públicos do governo.', 'easy', 'comprehensive', '["tesouro", "titulos"]'),
('O que é Imposto de Renda?', '["Taxa sobre ganhos", "Taxa sobre vendas", "Taxa sobre importação", "Taxa sobre serviços"]', 'Taxa sobre ganhos', 'IR é um tributo sobre a renda e ganhos de capital de pessoas e empresas.', 'easy', 'comprehensive', '["ir", "impostos"]'),
('O que é Liquidez?', '["Rentabilidade", "Facilidade de conversão em dinheiro", "Risco de investimento", "Taxa de juros"]', 'Facilidade de conversão em dinheiro', 'Liquidez refere-se à facilidade e rapidez de converter um ativo em dinheiro.', 'medium', 'comprehensive', '["liquidez", "conceitos"]'),
('O que é Rentabilidade?', '["Risco do investimento", "Retorno do investimento", "Taxa de juros", "Inflação"]', 'Retorno do investimento', 'Rentabilidade é o ganho ou perda obtido em um investimento em relação ao valor aplicado.', 'easy', 'comprehensive', '["rentabilidade", "retorno"]'),
('O que é Benchmark?', '["Tipo de investimento", "Parâmetro de comparação", "Taxa de corretagem", "Imposto"]', 'Parâmetro de comparação', 'Benchmark é um índice de referência usado para comparar a performance de investimentos.', 'medium', 'comprehensive', '["benchmark", "performance"]'),
('O que é Compliance?', '["Estratégia de investimento", "Conformidade regulatória", "Análise de mercado", "Gestão de risco"]', 'Conformidade regulatória', 'Compliance assegura que uma organização atenda a leis, regulamentos e padrões.', 'hard', 'comprehensive', '["compliance", "regulacao"]);